function addUser() {
	try {
		
		// Create and build message
		var restMessage = new sn_ws.RESTMessageV2('x_123228_portal.Add user', 'POST');
	
		// Changing roles from String to list of Strings
		var rolesList = [];
		var roles = current.role.split(',');
		for (var i = 0; i < roles.length; i++) {
			rolesList.push(roles[i]);
		}
		
		// Creating requestBody
		var user = { "email": current.email.toString(),
					"firstName": current.first_name.toString(),
					"information": current.short_description.toString(),
					"lastName": current.last_name.toString(),
					"roles": rolesList
		};
			
		var ticket = { "approval": current.approval.toString(),
					  "createdBy": current.sys_created_by.toString(),
					  "id": current.sys_id.toString(),
					  "openedOn": current.opened_at.toString(),
					  "requestedBy": current.request_state.toString(),
					  "state": current.sys_id.toString(),
					  "uniqueName": current.number.toString(),
					  "updatedOn": current.sys_updated_on.toString(),
					  "user": user
		};
		
		restMessage.setStringParameterNoEscape('tokenId', '1234');
		restMessage.setRequestBody(JSON.stringify(ticket));
		
		// Send message
		var response = restMessage.execute();
		var responseBody = response.getBody();
		var httpStatus = response.getStatusCode();
		
		// Get success state from response body
		var jsonObject = JSON.parse(responseBody);
		var successResponse = jsonObject.success;
		
		if (httpStatus == 200 && successResponse == true) {
			workflow.scratchpad.login = jsonObject.login;
			return "yes";
		} else {
			gs.error("Account creation was unsuccesfull for the user with following e-mail: {0}, code status: {1}, response body   {2}", current.email, httpStatus, responseBody);
			workflow.scratchpad.error = jsonObject.message;
			return "no";
		}	
	} catch(ex) {
		gs.error("Exception occured: ", ex);
	}
}

